@-moz-document domain("tarrmobiltv.hu") {
div#leftContainerContainer {
	position: fixed !important;
    overflow-y: scroll;
    bottom: 0;
    top: 100px;
    left: 0;
    width: 360px;
    background-color: rgb(0, 0, 0) !important;
}

.broadcast-item {
    width: 340px !important;
}

div#divMainPlayer {
 	position: fixed !important;
    left: 360px !important;
    top: 100px !important;
    height: calc(100% - 100px);
    width: calc(100% - 360px);
}

object#StrobeMediaPlayback {
    width: 100%;
    height: 100%;
}
}
