css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arstechnica.com") {
  body,main {
    background-color : #1b1b1b;
  }
  body,main, .comments-row #comments-area #comments > li > header .author > a, article.single .post-title, article.single .post-lowerdek, figure.intro-image figcaption, #social-footer a, .shortcode-caption {
    color: #FFF;
  }
  .post-tease::after {
    border-color: #DADADA;
  }
  .post-tease .post-meta {
    background-image: linear-gradient(to bottom,#171717 0,#121212 65%);
  }
  .post-tease .post-meta h2 a {
    color: #DADADA;
  }
  .comments-row #comments-area #comments > li > .body .quotecontent, .comments-row #comments-area #comments > li > header .rank, .comments-row #comments-area #comments > li > header .icons .timeago, .comments-row #comments-area #comments > li > footer .userinfo, .shortcode-blockquote .blockquote-content, .post-page blockquote .blockquote-content, .shortcode-blockquote .post-page blockquote p, .post-page blockquote .shortcode-blockquote p, .post-page blockquote p, figure.intro-image figcaption .credit a
  {
    color: #CFCFCF;
  }
  .pullbox, figure.intro-image figcaption, .shortcode-caption.caption {
    background: #0f0f0f;
  }
  .shortcode-caption {
    border-bottom: 0;
  }
  #author-footer {
    border-top-color: #FFF;
  }
  #social-footer, #recommendations-footer #story-recommendations, #recommendations-footer #latest-stories {
    border-top-color: #4a4a4a;
  }
}
