css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arstechnica.com") {
.shortcode-caption, .pullbox, .post-tease, .post-meta, body, main, figure.intro-image figcaption {
    background-color: #323232;
    background-image:  linear-gradient(to bottom,#323232 0,#323232 100%)!important;
  }
.comments-row #comments-area #comments > li > .body .quotecontent, .comments-row #comments-area #comments > li > header .author, #author-footer .author-social-footer strong, .comments-row #comments-area #comments > li > .body, .shortcode-caption, .post-tease .post-meta h2 a, figure.intro-image figcaption, figure.intro-image figcaption .credit, .post-lowerdek, article.single .post-title, h1, h2, h3, h4, h5, h6, p {
    color: beige!important;
  }
  
  
.post-tease::after, #recommendations-footer #story-recommendations, #recommendations-footer .heading-column, #recommendations-footer #sponsored-recommendations,  footer#footer {
    display: none
  }
#recommendations-footer #latest-stories, #author-footer, .comments-row #comments-area #comments > li {
    border: none;
  }
 header#header, .post-tease .post-meta {
    margin-top: 0px;
   border: none;
  }
  .post-tease .comment-count {
    display: none;
  }
  
  .index-row .post-tease, .index-row.features .post-tease:first-child, .index-row.features .post-tease:last-child {
    width:50%!important;
  }
  
  .row {
    display: table;
    margin: auto;
  }
  
  .post-tease figure {
    padding-bottom: 25%;
  }
  
  .post-tease figure div {
    height: 200px;
  }
  
  .comments-row #comments-area .pager .comment-pagination {
    float: none;
  }
}
