css:
@-moz-document url-prefix("http://arstechnica.co") {
body, main {
    background-color :#EFEFEF;
}

section.xrail, .row.ad-row, .ad.ad_crown, .cns-ads-container {
 display:none !important;
}
.index-row .post-tease {
    max-width:50% !important;
}

.index-row .posts-column {
    max-width:none;
    min-width:0;
}

.features article:before {
    content:'';
    position:absolute;
    margin-left:-20px;
    z-index:9;
	border-left:solid #fa501e 3px;
}
.index-row .post-tease {
    width: calc(33% - 20px) !important;
    max-width:40%;
    min-width:0;
    
    -webkit-flex-basis: auto !important;
    flex-basis: auto !important;
}
.index-row .post-tease figure {
	padding-bottom:30%;
}
.post-tease .post-meta h2 a {
    font-size:20px;
    line-height:27px;
}
.post-tease:after {
    height: 3px;
    border-width: 3px;
}
.post-tease .comment-count {
    left:auto;
    right:3px !important;
    bottom: 3px;
    background-image:none !important;
    background-color:#fa501e !important;
    color:#FFF !important;
}

#sponsored-recommendations {
    display:none !important;
}


}
