css:
@-moz-document domain(mingle.thoughtworks.com) {

  #header, #cta_frame_wrapper { display: none; }

  .card-icon {
    width: 96%;
    height: 175px;
    margin: 4px; 
    max-width: 96%;
  }
  .card-inner-wrapper {
    padding: 0 0 0 2px;
    font-size: 24px;
  }
  .card-inner-wrapper .colored-tags {
    font-size: 60%;
    top: 0;
    left: 0;
    position: relative;
    height: 20px;
    transform: inherit;
  }
  .card-summary-number {
    font-size: 14px;
  }
  .card-summary-number a {
    text-decoration: none;
  }

}
