css:
@-moz-document domain("mturkcrowd.com") {
html,
html > body,
.thread_view,
.pageWidth,
.pageContent
{
    color            : #FFFFFF;
    background-color : #000000 !important;    
}

.SpoilerTarget.bbCodeSpoilerText,
.attribution.type,
.quoteContainer,
.navTabs,
.sectionMain.message
{
    border           : #1A1A1A 1px solid !important;
    color            : #FFFFFF;
    background-color : #0D0D0D           !important;
}


.PageNav > a,
.PageNav > nav > a,
.PageNav > nav > .scrollable > .items > a
{
    color            : #FFFFFF;
    background-color : #0D0D0D           !important;
}

.uix_textLogo
{
    color            : #197E97;
}

.likesSummary.secondaryContent
{
    color            : #FFFFFF !important;
    background-color : #0D0D0D !important;
}

button,
.publicControls > *,
.privateControls > *
{
    font             : bold 12px verdana, arial, sans-serif;
    color            : #197E97 !important;
    background-color : #1A1A1A !important;
    border-color     : #262626 !important;
}

.avatarHolder,
.userText,
.ribbon,
.extraUserInfo
{
    background-color : #0D0D0D           !important;
}

.ctaBbcodeTable *,
.baseHtml.signature.messageText.ugc
{
    border-color     : #FFFFFF !important;
}
/****** Changes link colors ******/
a:link,
.PageNavPrev 
{
    color : #197E97;
}

a:visited
{
    color : #FFFFFF;
}

a:hover
{
    color : hotpink !important;
}

a:active
{
    color : #FFFFFF;
}
}
