css:
@-moz-document domain(gw2efficiency.com) {

html, body {
    background-color: #333232;
    color: white;
}

.rarity-1, .rarity-1 * {
    color: #FFF;
}

.navigation li a {
    color: #FFFFFF;
}

.navigation li.active>a>span {
    text-shadow: none;
    font-weight: bold;
    font-size: 125%;
}

.component-tree .display .prices .price.selected {
    background-color: rgb(0, 0, 0);
}

.component-tree .display .prices .price {
    background-color: black;
}

}
