css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("disposablechat.com") {
  
  body {
    min-width: 820px;
}
body, html {
    background-color: #ffffff;
    font-family: verdana;
    margin: 0;
    padding: 0;
}
    .wrap {
    background-image: url("http://www.disposablechat.com/resources/bg_middle.gif");
    margin: 0 auto;
    width: 820px;
}
    .footer {
    background-image: url("http://www.disposablechat.com/resources/bg_bottom.gif");
    clear: both;
    height: 33px;
    position: relative;
    top: 5px;
}
.header img {
    margin-left: 3px;
    visibility: hidden;
}
    
element.style {
    background: white none repeat scroll 0 0;
}
dl {
    background: white none repeat scroll 0 0;
    border-style: solid;
    border-width: 1px;
    height: 200px;
    margin: 0;
    overflow: auto;
    padding: 2px;
    width: 490px;
}
    ul.users {
    color: DarkGreen;
    height: 194px;
    list-style: outside none none;
    margin: 5px 0 12px;
    overflow: auto;
    padding-left: 5px;
}
    .footer p {
    left: 29px;
    padding: 5px 0;
    position: relative;
    top: -10px;
    visibility: hidden;
}
}
