css:
@-moz-document domain("stefangagne.com") {
.entry-comments, #respond {
	display: none;
}
body {
	background-color: #222;
	color: #ddd;
}
#content {
	max-width: 80%;
	margin: auto;
}
a:link {
  color: #7F869E;
}
a:visited {
  color: #677194;
}
@media(max-width: 600px) {
	#content {
		max-width: 95%;
	}
}
}
