css:
@-moz-document url-prefix("http://sbazar.cz"),
               url-prefix("https://sbazar.cz"),
               url-prefix("http://www.sbazar.cz"),
               url-prefix("https://www.sbazar.cz"),
               regexp("https?://[a-zA-Z0-9]+\.dev(:[0-9]+)?/.*")
{

  #mrEgg
  {
    position: relative;
    box-shadow: 0 1px 2px rgba(0,0,0,0.3), 0 2em 6em rgba(0,0,0,0.33);
  }

  #mrEgg .image img
  {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
  }

  #mrEgg #statusMessagePlaceBox
  {
    position: relative;
    /* box-shadow: -1px 0 0 0 rgba(0,0,0,.3), 1px 0 0 0 rgba(0,0,0,.3), 0 -1px 0 0 rgba(0,0,0,.3); */
  }

#mrEgg #mainPhoto {
    margin: -50px -50px 40px -50px;
}

#mrEgg .fotka img {
    width: 100%;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
}
