css:
@-moz-document domain("chatango.com") {
#OM .msg u,
#OM .msg b,
#OM .msg i,
#OM .msg span {
    display: inline-block !important;
}
#OM .msg .thumb,
#OM .msg .thumb-popup:hover {
    display: inline-block !important;
}

#OM .c_username{
padding-right: 5px;
}

#OM .msg u:first-letter,
#OM .msg b:first-letter,
#OM .msg i:first-letter,
#OM .msg span:first-letter,
span:first-letter,
#OM .msg p:first-letter {
    text-transform: capitalize !important;
}
#OM .c_username {
    text-transform: capitalize;
    font-weight: bold !important;
}
#OM .filtered {
    display: none !important;
}
}
