css:
@-moz-document domain("calendar.google.com") {
.st-grid .st-dtitle:nth-child(5n+6):not(.st-dtitle-nonmonth),
.st-grid .st-dtitle:nth-child(5n+7):not(.st-dtitle-nonmonth) {
    background-color: #b5b5b5;
}
.st-grid .st-dtitle.st-dtitle-nonmonth:nth-child(5n+6),
.st-grid .st-dtitle.st-dtitle-nonmonth:nth-child(5n+7) {
    background-color: #cecece;
}
.mv-dayname:nth-child(5n+6),
.mv-dayname:nth-child(5n+7) {
    color: red;
    font-weight: bold;
}
.st-bg-table .st-bg:nth-child(5n+6),
.st-grid .st-dtitle:nth-child(5n+6):not(.st-dtitle-nonmonth),
.st-grid .st-dtitle.st-dtitle-nonmonth:nth-child(5n+6) {
    border-left: 1px solid red;
}
}
