css:
@-moz-document domain("theverge.com") {
ul.p-entry-nav,
div.m-entry__sidebar,
div.p-rock,
a.p-circuitbreaker-logo,
div.m-big-stories,
ul.m-header__social,
a.m-new-articles__toggle,
ul.p-entry-header__byline,
div.m-article__share-buttons,
div.m-linkset,
div.m-footer {
    display: none !important;
}

div.m-header__logo {
    max-height: 22px !important;
    padding-top: 2px;
    width: 170px;
}

div.m-entry__sidebar {
    width: 0px !important;
}

svg.verge-logo {
    max-height: 18px !important;
    max-width: 140px;
}

header {
    margin-top: 0px !important;
}

h1 {
    font-size: 2.5em !important;
}

div.m-article__entry,
div.m-article__entry-video {
    width: 100%;
}

q {
    font-size: 1.3em !important;
    display: none !important;
}
}
