css:
@-moz-document url-prefix("https://tweetdeck.twitter.com/") {
.column {
    height: 49%;
    display: block;
    flex-shrink: 0;
}
.column:hover {
    height: 90%;
}
.column:nth-child(even):hover {
    height: calc(80vh + 50px);
    margin-top: calc(-41.5vh - 3.5% + 50px);
}
.horizontal-flow-container {
    display: flex;
    -webkit-flexwrap: wrap;
    flex-wrap: wrap;
    -webkit-flex-direction: column;
    flex-direction: column;
}
}
