css:
@-moz-document domain(santander.co.uk) {
/* Mask private info, until hover */
.number, 
.balance,
#sel_account,
td.currency {  -webkit-filter: blur(5px); }
.number:active, 
.balance:active,
#sel_account:active,
td.currency:active { -webkit-filter: blur(0px); }

/* Reduce clutter Main */
.auxinfo.aCenter,
.message.info,
.genericBanner,
.announcement,
#pagetools,
.editlist,
#InteriorSmall,
#alertsToolbox,
#contactus,
#footer {display: none !important;}

.toolbox {border-bottom: 0px;}

#content h1 {
    margin-bottom: 0;
}
.pendingMessages {
    float: right;
}

#auxcontentBar {display: none !important;}
#bodycontent { width: 818px; }

#content.myaccounts ul.accountlist .name a { font-size: 2em; }

/* Reduce clutter acc */
.imageMessage {display: none;}
}
