css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("app.gistboxapp.com") {

    @media ( max-width: 1030px ) {
        /* top bar */
        .navbar .brand,
        .navbar .header-divider,
        .navbar .nav,
        .navbar .header-signed-in {
            display: none !important;
        }
        .navbar .header-controls {
            float: left;
            display: block;
            position: relative;
            margin: 15px 15px 0 0;
            left: 0;
            top: 0;
        }
        .navbar .header-searchbox {
            float: left;
            display: block;
            position: relative;
            left: 0;
            top: 0;
            margin: 10px 0 0 0;
        }
        /* content */
        .library-view {
            min-width: auto;
        }
        .library-view .sidebar-nav-fixed {
            display: none;
        }
        .library-view .content-view-container {
            padding-left: 0;
        }
        .split-view .split-view-list {
            position: relative;
            background-color: #fff;
        }
        .split-view .split-view-content {
            position: relative;
            background-color: #fff;
        }
    }

    @media ( max-width: 710px ) {
        /* content */
        .split-view .split-view-list {
            display: block !important;
            width: 100%;
            height: 25vh !important;
            overflow: hidden !important;
            border-right: 0;
            border-bottom: 2px solid #54b4eb;
        }
        .split-view .split-view-content {
            display: block;
            width: 100% !important;
            height: 75vh !important;
            overflow: hidden;
            left: 0;
        }
        .split-view-content .focus-view-gist {
            height: 90% !important;
        }
        .split-view-content .focus-view-gist .focus-view-header {
            height: 18% !important;
        }
        .split-view-content .focus-view-gist .focus-view-content {
            height: 78% !important;
        }
    }

    @media ( min-width: 711px ) and ( max-width: 1030px ) {
        /* content */
        .split-view .split-view-list {
            float: left;
            width: 37% !important;
            border: 0;
        }
        .split-view .split-view-content {
            float: left;
            width: 63% !important;
            left: 0;
            display: block;
        }
    }

}
