css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("1fichier.com") {
  body {
    background: #444;
    color: #aaa;
  }
  
  a {
    color: #aaa;
  }
  
  #fu, .bloc, #header, td.normal, td.normal3a ,td.normal3b, div.bloc2 {
    background: #666 !important;
  }
  
  td.orange {
    background: #F29C4E !important;
  }
  
  input, span.selected, li {
    background: #888 !important;
    color: #eee;
  }
  
  .selected::after, .btn-orange {
    background: #888 !important;
    box-shadow: inset -55px 0 25px -20px #888 !important;
  }
  
  .btn-orange:hover, a:hover {
    color: #EF7C14;
  }
  
  div.droite h2 a img {
    filter: invert(0.75);
  }
}
