css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pourchienetchat.com") {
  body, #top-menu {
    background: #777 !important;
    color: #ccc !important;
  }
  
  a, td, input, select, p, .addresses li div, th, h3 {
    color: #ccc !important;
  }
  
  #footer_fidelity div a, .button, .big_green, .idTabs li a, h1, button, .warning, #home_message div a {
    color: #666 !important;
  }
  
  .subtab, th, .product_block, #searchbox p, #search_query {
    background: #666 !important;
  }
  
  .product_block:hover {
    background: #787878 !important;
  }
  
  #searchbox p button {
    background: #666 url(http://pourchienetchat.com/themes/diggle/img/sprites.png?20160429135900) no-repeat;
    background-position: -195px 0;
  }
}
