css:
/*
 * Slack RTL
 */
#message-input, .message {
    direction: rtl;
    font-family: "Droid Arabic Kufi", Tahoma, Arial !important;
    font-size: 15px !important;
}

code, pre {
    direction: ltr;
    text-align: left;
    unicode-bidi: embed;
}
.message .member_image {
    left: auto !important;
    right: 0;
    float: right !important;
}

.message {
    padding: .25rem 3rem .1rem 2rem !important;
}

.message .msg_actions {
    right: auto !important;
    left: 0;
}

.message .message_star_holder {
    left: auto !important;
    right: 27px;
}

.message .timestamp {
    right: 2px;
    left: auto !important;
}

ts-message {
	width: 95% !important;
}

.light_theme ts-message .message_gutter {
	padding-left: .625rem;
    padding-right:0;
 	right:0;
    left:auto;
    top: 5px;
}

.light_theme ts-message.first .message_content .message_sender {
	float: right;
}

/* i really want this to be global */
