css:
@-moz-document domain(pirates-forum.org) {  
#logo{
    background: black;
    border-width: 1px;
    border-bottom-color: #999;
  }
  #panel .upper{
  	background: #0F0F0F url("https://pirates-forum.org/images/tcat.png") repeat-x scroll 0% 0%;
    padding: 4px;
    border-bottom-width: 1px;
    border-bottom-color: #999;
  }
  #panel .lower{
  	background: black;
    border-top-width: 0px;
    color: white;
  }
  #panel .lower ul.user_links, #panel .lower a:link {
    color: white;
  }
  #panel .lower ul.user_links li a {
    color: white;
  }
  table.tborder .thead {
    background: black;
    border: black;
    padding: 4px;
    border-radius: 0px;
  }
  #cat_28, a:link, #cat_28, a:visited {
   color: black;
  }
  #cat_28, a:hover {
    text-decoration: underline;
  }
  tr td.tcat {
    padding: 4px;
  }
  #logo a img {
    height: 0px !important;
    width: 0px !important;
    padding-left: 349px !important;
    padding-top: 60px !important;
    background: url(http://i.imgur.com/Dk3lRWM.png) no-repeat !important;
  }
  #panel .lower {
    padding: 1px;
  }
  input.textbox {
    border-radius: 0px;
    padding-right: 50px;
  }
  input.button, .tborder, a.button.new_thread_button{
    border-radius: 0px;
  }
}
