@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sheltermanager.com") {


.asm-thumbnail {
    vertical-align: middle;
    border: 1px solid #000;
    background-color: #ffffff;
    padding: 6px;
    margin: 5px;
    max-width: 100px;
    max-height: 100px;
    border-radius: 3px;
    
}
.asm-thumbnail-male {
    background-color: #CA6AB1;
    
}
.asm-thumbnail-female {
    background-color: #0044DC;
    

}
.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default {
    border: 1px solid #007EFF;
   
    font-weight: bold;
} 
.asm-quicklinks a {
    display: inline-block;
    white-space: nowrap;
    border-radius: 3px;
    box-shadow: 0 1px 3px #DDD;
    border: 1px solid #555;
    padding: 5px;
    margin: 1px;
    font-weight: bold;
}
.ui-widget-content a {
    color: #FFFFFF;
    font-weight: bold;
}
}/* end */
