    font-size: 14px !important;
    font-family: "Droid Sans Mono", monospace !important;
    letter-spacing: -0.5px;
  	padding-right: 40px !important;
}
.mm_textField input {
    font-size: 14px !important;
    font-family: "Droid Sans Mono", monospace !important;
    letter-spacing: -0.5px; 	 
}
