css:
@-moz-document url-prefix("https://meh.com/forum"), url-prefix("http://meh.com/forum") {
@media screen and (max-width:576px) {
	#add-comment-desktop {
		display: block;
		margin: 32px auto;
		width: 304px;
	}
	.editor textarea {
		font-size: 100% !important;
		height: 73px !important
	}
	li[id*="wmd-bold-button"],
	li[id*="wmd-italic-button"],
	.wmd-spacer1,
	li[id*="wmd-code-button"],
	li[id*="wmd-heading-button-reply"],
	li[id*="wmd-hr-button-reply"],
	.wmd-spacer3,
	li[id*="wmd-undo-button"],
	li[id*="wmd-redo-button"],
	.wmd-spacer4,
	li[id*="wmd-emoji-button"],
	#add-comment-mobile-footer {
		display: none;
	}
	li[id*="wmd-link-button"],
	li[id*="wmd-quote-button"] {
		margin-left: -75px;
	}
	li[id*="wmd-image-button"],
	li[id*="wmd-olist-button"],
	li[id*="wmd-ulist-button"],
	li[id*="wmd-heading-button"],
	li[id*="wmd-hr-button"] {
		margin-left: -100px;
	}
	.wmd-spacer2 {
		margin-left: -89px;
	}
	.image-uploader-wrapper {
		width: 90%;
		margin-left: -47%;
		height: 170px;
		margin-top: -93px;
	}
	.image-uploader #uploader-button,
	.image-uploader .image-selector .or {
		float: none;
		display: block;
	}
	.image-uploader .drag-instructions {
		display: none;
	}
}
}
