css:
@-moz-document domain("wikiwand.com") {
html, .invite_box a, img:not([class*=tex]), *[style*=background-image] {
    -webkit-filter: invert(1);
    -o-filter: invert(1);
    filter: invert(1);
}
.article_btn { display: none }
#wikiwand_toc { background-color: #D5D6DD !important }
#toc_btn { background-color: #D7D8DF !important }
#toc_btn > span { color: #666 !important }
#wikiwand_toc .toc_list > li.active {
    background-color: #C9CAD4 !important;
}
#wikiwand_toc .toc_list>li>a.active, #wikiwand_toc .toc_list>li>a:hover {
    color: #5781FF;
}
#wikiwand_toc .toc_list>li:hover {
    background-color: #C9CAD4;
}
#wikiwand_toc .toc_list>li>a {
    color: #999;
}
}
