@-moz-document domain("wacai.com") {

  #siteNav{display: none;}     /* 顶部菜单 */
  
  .logo{display: none;}   /* logo */
  
  .sub-nav{display: none;} 
  
  nav.nav > ul:nth-child(1) > li:nth-child(4){display: none;} 
  
  #DZNews{display: none;} 
  
  #layer{display: none;} 
  
  .footer{display: none;} 
  
  #live800{display: none;} 
  
  .header {
	height: 0px;
	padding: 0;
  }
   
  .login-info, .sub-nav {
	background: #f5f5f5;
  }
  
  .login-info dt, .sub-nav dt {
	background: #f5f5f5;
  }
  
  .header .nav {
	line-height: 15px;
  }
  


  /* 论坛 */
  
  #nv{display: none;} 
  
  .hdc{display: none;} 

  .comiis_subnav_wrap{display: none;} 
  
  .comiis_left_divs{display: none;} 
  
  div.bm:nth-child(1){display: none;} 
  
  #ft{display: none;} 
  
  .m-bottom{display: none;} 
  
  .s_seo{display: none;} 
  
  div.area:nth-child(9){display: none;} 
