css:
@-moz-document domain("www.notengotele.com") {
body {
    background: black !important;
    color: #66b2fe;
}
#content #featured_movie, .comment_box {
    background: #4b4b4b !important;
}
#content #featured_movie .title {
    background: #4b4b4b !important;
}
#content #featured_movie .title a, .userbox,.comment_box p a {
    color: #6bcbef !important;
}
#content #featured_movie .by {
    color: white;
}
#sidebar .widget {
    background: #4b4b4b !important;
}
#sidebar #categories li a {
    color: white;
}
#sidebar .widget h4 {
    color: #66b2fe;
}
#sidebar .tags a {
    color: #6bcbef !important
}
#content #featured_movie .tags a {
    background-color: black;
    color: white;
}
#content #featured_movie #controls .favoritos {
    color: #6bcbef !important
}
.tags a {
    visibility: hidden;
}
.tags:hover a {
    visibility: visible !important;
}
.expl {
    background: #4b4b4b !important;
}
h4 a {
    color: #66b2fe !important;
}
h5 a {
    color: #6bcbef !important;
}
.more a {
    color: white !important;
}
.comment_votes{
 color: black;   
}
div.comment_box p{
 color: white !important;   
}
#content .section_title,#content .section_title h2 a{
    color: #66b2fe !important;
    text-shadow: 0 0px 0 #ffdb00 !important;
}
#content .video_item{
    background: #4b4b4b !important;
    height: 220px;
}
#content .video_item .video_stats{
    margin-top: 210px;
}
#content .video_item .video_info h3 a{
 color: white !important;   
}
#content .video_item a.link{
color: #6bcbef !important;
}
.textbox a{
 display:none;   
}
}
