css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("slovnik.seznam.cz") {
/* Hide ads and other spaceeaters */
div#column, div#fox-promo, div#head h1, div#head #seznam, li.active, #search #words .word a img, div#tabs {display:none !important;}
/* Compact layout */
div#main {margin:0px 0px 0px 4px !important;}
div#main, div#head, div#roof {min-width:99.6% !important;width:99% !important;}
/* Head and search form */
#head {margin-bottom:0px !important;}
#head #searchform {margin-left:0px !important;padding:0px !important;}
div#context {margin:4px 0px 0px 0px !important;padding:5px !important;}
div#context p {margin-top:0px !important;}
div#roof {padding:0px !important;}
form#searchform {margin:0px !important;}
form#searchform input.query {width:auto !important;}
span.inputs select {height:24px;position:relative;left:-3px;top:1px;}
#head #searchform .inputs .submit {position:relative;left:-6px;top:-0.7px;}
/* Results */
#search #words .word {text-align:left !important; width:30px !important;}
#results {margin-left:-3px !important;}
#paging {text-align:left !important;}
}
