/* ==UserStyle==
@name           wiki.SuperFamicom.org Dark
@namespace      gitlab.com/Neui/userstyles
@homepageURL    https://gitlab.com/Neui/userstyles
@supportURL     https://gitlab.com/Neui/userstyles/issues
@version        2.0.0
@license        CC-BY-4.0
@description    Dark mode for wiki.superfamicom.org
@author         Neui
==/UserStyle== */

@-moz-document domain("wiki.superfamicom.org") {
	html, body {
		background-color: black;
		color: white;
	}

	a {
		color: #D7D7D7;
	}

	a:hover, a:focus {
		color: #EEE;
	}
	.content a:hover {
		color: #FFF;
	}

	/* Edit button */
	.before-main-wrapper .header-wrapper .header-titles .edit-document {
		border-color: grey;
		color: darkgrey;
	}

	/* Tables */
	#content tr td {
		background: #303030;
	}

	#content tr:nth-child(2n) td {
		background: #3F3F3F;
	}

	#content tbody tr:hover td {
		background: #555;
	}

	#content td, #content th, #content table, #content tbody, #content thead {
		border-color: #666;
		border-bottom: 1px solid #666;
	}

	/* Code */
	pre {
		/* Using the invert filter to deal with syntax highlighting */
		-webkit-filter: invert(85%);
		filter: invert(85%);
	}

	pre, code {
		/* Fixes <code> in tables */
		background-color: inherit;
		color: inherit;
	}

	span.hljs-comment { /* Fix comments otherwise they are somewhat hard to read */
		color: #554;
	}

	/* Table of Contents (the box to the right) */
	.toc {
		background: #252525;
		border-color: #111;
	}

	/* Editing and creating */
	form textarea.form-control, form input[type=text] {
		background: black;
		color: white;
		border-color: grey;
	}

	form textarea.form-control::placeholder, form input[type=text]::placeholder {
		color: darkgrey;
	}

	form#fileupload {
		background: black;
		color: white;
		border-color: grey;
	}

	.form-control:focus {
		border-color: #D7D7D7;
	}

	/* Footer */
	.content-sections .section h2 {
		color: #EEE;
	}

	.content-sections .section ul li a {
		color: #EEE;
	}
}
