css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-strip { height: 25px !important; }
.tabs-bottom { display: none !important; }

.tabbrowser-tab {
  margin: -2px 2px 4px 0 !important;
  padding: 0 0 2px 0 !important;
  border-right: 1px solid !important;
  border-left: 1px solid !important;
  border-bottom: 1px solid !important;
  -moz-border-bottom-colors: ThreeDShadow !important;
  -moz-border-right-colors: ThreeDShadow !important;
  -moz-border-radius-bottomleft: 8px !important;
  -moz-border-radius-bottomright: 8px !important;
  }
.tabbrowser-tab:not([selected="true"]):hover {
  margin: -3px 2px 4px 0 !important;
  padding-top: 2px !important;
  -moz-border-radius-bottomleft: 5px !important;
  -moz-border-radius-bottomright: 5px !important;
  }
.tabbrowser-tab[selected="true"] {
  margin: -3px 2px 4px 0 !important;
  padding-top: 2px !important;
  -moz-border-radius-bottomleft: 5px !important;
  -moz-border-radius-bottomright: 5px !important;
  }

.tabs-alltabs-button {
  margin: 0 !important;
  border-top: 0 !important;
  border-left: 1px solid !important;
  -moz-border-radius-topleft: 0 !important;
  -moz-border-radius-topright: 0 !important;
  }
.tabs-alltabs-button:hover { padding-bottom: 2px !important; }

.tabs-newtab-button {
  margin: -0 0 4px 0 !important;
  padding: 1px 0 0 3px !important;
  min-width: 18px !important;
  border-top: 0 !important;
  border-bottom: 1px solid !important;
  -moz-border-bottom-colors: ThreeDShadow !important;
  -moz-border-radius-bottomleft: 8px !important;
  -moz-border-radius-topleft: 0px !important;
  -moz-border-radius-topright: 0 !important;
  }
.tabs-newtab-button:hover {
  padding: 1px 0 0 3px !important;
  -moz-border-radius-bottomleft: 5px !important;
  }

.tabbrowser-tab .tab-text { border: none !important; }
