/* PeerAnswer PeerAnswer Exclamation Mark Notification by geerky42 */

@-moz-document url-prefix('https://www.peeranswer.com/') {
  .icon_bubble {
    text-indent: -9999px;
  }
  .icon_bubble:after {
    text-indent: 0px;
    float: left;
    content: '!';
  }
  div.icon_bubble {
    width: 4px;
  }
}
