css:
@-moz-document domain("poesiaedgar.blogspot.mx") {
body {
    font-size: 1.3em;
    font-weight: 100;
    color: #555;
    background: #FFF;
    font: none;
    font-family: Garamond, serif;
}
.post-outer {
    border: none;
    background: none;
}
.columns-inner .column-right-outer {
    display: none;
}
.main-inner .fauxcolumn-right-outer {
    display: none;
}
.main-inner .columns {
    padding-right: 0;
}
a:link {
    color: #336E7B;
}
a:visited {
    color: #67809F;
    text-decoration: line-through;
}
}
