css:
@-moz-document domain("www.macrumors.com") {
a:link {
    color: black;
    font-family: 'Source Sans Pro',Arial,sans-serif;
    font-size:14px;
}
a:visited {
    color: black;
}
a:hover {
    color: gray;
}
a:active {
    color: black;
}

#header{
	width:100%;
    padding-top:50px;
    padding-bottom:50px;
    border-radius: 0;
}

.brand {
    content:url(http://i.imgur.com/di2YY0z.png);
    width:500px;
    height:auto;
}

#header .navigation {
border: none;
    box-shadow: none;
    border-radius: 0;
}



#header .secondary {
border: none;
    border-radius: 0;
}

div.article {
border: none;
    width:100%;
    margin-left:200px;
}

*,* *,blockquote,.article .content blockquote,.article .content a,.article .content ul, .article .content ul li, .article .content table, .article .content table tr, .article .content table tr td
{
	font-size: 22px;
    line-height: 1.5;
}
.article,body,html,h1,h2,h3,h4,h5,p,span,ul,li,ol,form,div,blockquote,span {
    font-family: 'Source Sans Pro',Arial,sans-serif;
    background-image: none; 
    color: rgba(0,0,0,1) !important;
	background-color: rgba(255,255,255,.5) !important;
}
h1.title {
font-size: 1.5em !important;
}
div.content {
 font-size: 1em !important;
}


.article.blog .header h1{
	width:65%;
}


.article .title a, .tagged .article.excerpt .title a, .author .article.excerpt .title a {
	color: black;
    font-size:30px;
}
}
