css:
@-moz-document domain("bonpatron.com"), domain("spellcheckplus.com") {
  #top { display: none;}
  #belowmain { display: none;}
  body {background-color: #282829;}
  #langtoolresults, #summarycontainer, #legend td, .opts, a, a:link {color: white;}
  #langtoolresults h1, .hpTipLink {color: red;}
  .mainbutton, .mainbutton:visited {background-color: black; border: 1px solid white;}
  .defaultSkin table.mceLayout tr.mceLast td {display: none;}
  .defaultSkin td.mceToolbar {background: none;}
  .defaultSkin table.mceLayout tr.mceFirst td {border: none;}
  .defaultSkin table, .defaultSkin tbody, .defaultSkin a, .defaultSkin img, .defaultSkin tr, .defaultSkin div, .defaultSkin td, .defaultSkin iframe, .defaultSkin span, .defaultSkin *, .defaultSkin .mceText {background: white;}
  #langselect {display:none;}
  #langtoolresults {background: none; color: red;}
  #langtoolresults table table {color: black; border: solid 1px black;}
}
