css:
@-moz-document domain(topvisor.ru), domain(topvisor.com) {
	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body td{ height: 28px; }
	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body .hcol td.phrase div.middle{ height:28px; }
	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body .cols td div{ min-height:28px; line-height:22px; }
	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body .cols td div a{ font-size: 14px; text-decoration: none; }
	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body .cols td div .p [class*=icon-]{ font-size: 12px; }

	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body .hcol td.phrase div .recheck,
	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body .hcol td.phrase div .del{ top:8px; right:1px; }
	#dynamics_table:not(.with_snippets):not(.show_global_frequency) .body .hcol td.phrase div .del{ right:20px; }
}
