css:
@-moz-document domain("trello.com") {
    /* Card count on Card */
    .list-header-num-cards  {
        display: inline !important;
        padding-top: 4px;
    }
    
    /* Label Text on Labels */
    .list-card-labels .card-label {
        line-height: 20px;
        height: auto;
        padding: 0 4px;
        width: auto;
        font-size: 11px;
        font-weight: normal;
        text-shadow: 0px 0px 2px #000;
        margin: 0 0 4px;
    }
    
    /* Remove strikethrough from Completed Checklist Items */
    .checklist-item-state-complete .checklist-item-details-text {
        text-decoration: none;
    }
    
    /* Smaller "Add new list" link */
    .list-wrapper.mod-add.is-idle {
        width: auto;
    }
}
