css:
@-moz-document domain("confluence.gfk.com") {
body {
  font: caption !important;
  font-size: 18px !important;
  line-height: 1.55 !important;
}

#header {
  font-size: 12px;
}

.innerCell {
  max-width: 40em !important;
}

#main {
  padding: 3em !important;
}

.cell.aside .innerCell {
  padding: 1em;
  background: #efefef;
}

.cell.aside img {
  display: none;
}

.plugin_pagetree_children_list .plugin_pagetree_childtoggle_container,
.plugin_pagetree_children_list .plugin_pagetree_children_content {
  float: none !important;
  display: inline-block !important;
  vertical-align: top;
}

.plugin_pagetree_children_container:empty {
  height: 50px;
  width: 100%;
  background: red;
}

.plugin_pagetree_children_list li {
  padding-top: 0.2em !important;
  padding-bottom: 0.2em !important;
  border-bottom: 1px solid #ccc !important;
}

.plugin_pagetree_children_list li:last-child {
  border-bottom: none !important;
}

.plugin_pagetree_childtoggle_container > .icon {
  position: relative !important;
  top: 0% !important;
  display: inline-block;
  vertical-align: middle;
  margin-top: -4px;
  text-indent: 0 !important;
  background: none !important;
  height: 1em !important;
  width: 1em !important;
  line-height: 1 !important;
}

.plugin_pagetree_childtoggle_container > .icon:after {
  content: "\25b6";
  font-size: 12px;
  display: inline-block;
}

.plugin_pagetree_childtoggle_container > .icon.icon-section-opened:after {
  content: "\25bc" !important;
}

.plugin_pagetree_childtoggle_container > .icon.no-children {
  display: none;
}

#logo,
.app-switcher-trigger,
.aui-nav li:nth-child(2),
.aui-nav li:nth-child(3),
.aui-nav li:nth-child(4) {
  display: none !important;
}
}
