/* STYLE BY ADALEIGHFAITH || TUMBLR & INSTAGRAM */

::-webkit-scrollbar {
background: transparent;
height: 7px;
scrollbar-arrow-color: #222222;
width: 9px;
}

::-webkit-scrollbar-thumb {
-webkit-border-radius: 20px;
background: #222222;
border: 1px solid #616161;
min-height: 5px;
min-width: 5px;
}

::-webkit-scrollbar-thumb:active {
-webkit-border-radius: 20px;
background: #616161;
border: 1px solid #222222;
}

::-webkit-scrollbar-thumb:hover {
-webkit-border-radius: 20px;
background: #9e9e9e;
border: 1px solid #616161;
min-height: 5px;
min-width: 5px;
}

/* UP ARROW */
::-webkit-scrollbar-button:vertical:decrement {
background-image: url(http://i.imgur.com/GzKEnC2.png);
height: 7px !important;
width: 9px !important;
}

/* DOWN ARROW */
::-webkit-scrollbar-button:vertical:increment {
background-image: url(http://i.imgur.com/Yvf4yzY.png);
height: 7px !important;
width: 9px !important;
}

/* LEFT ARROW */
::-webkit-scrollbar-button:horizontal:decrement {
background-image: url(http://i.imgur.com/RIjPW8Y.png);
height: 9px !important;
width: 7px !important;
}

/* RIGHT ARROW */
::-webkit-scrollbar-button:horizontal:increment {
background-image: url(http://i.imgur.com/oITuIQJ.png);
height: 9px !important;
width: 7px !important;
}
