css:
@-moz-document domain("twitch.tv") {
body {
    visibility: hidden;
    margin-left: 0px !important;
    margin-right: 0px !important;
}
#main_col.expandLeft,
#flash.expandLeft {
    margin-left: 0px;
    margin-right: 0px;
}
#channel .player-column #player,
#channel .player-column #video-1 {
    visibility: visible;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 999;
    width: 100% !important;
    height: 100% !important;
}
.player-hover {
    visibility: hidden;
}
#left_col,
#right_col {
    width: 0px;
}
}
