css:
@-moz-document url-prefix("http://socialani.me/") {
.pref-avatar:after {
	content: '';
	position: absolute;
	top: -2px; 
    right: 0px;
	bottom: 0; 
    left: 1px;
    width: 64px!important;
    height: 64px!important;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    background-size: 100% 100%!important;
    z-index:20!important;
}
.info-user:after {
	content: '';
	position: absolute;
	top: -2px; 
    right: 0px;
	bottom: 0; 
    left: 1px;
    width: 40px!important;
    height: 40px!important;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    background-size: 100% 100%!important;
    z-index:20!important;
}
li.node-response div.body:after {
	content: '';
	position: absolute;
	top: -2px; 
    right: 0px!important;
	bottom: 20; 
    left: -50px;
    width: 40px!important;
    height: 40px!important;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    background-size: 100% 100%!important;
    z-index:20!important;
}
li.node.comment:after{
	content: '';
	position: absolute;
	top: -2px; 
    right: 0px;
	bottom: 0; 
    left: 0px;
    width: 40px!important;
    height: 40px!important;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    background-size: 100% 100%!important;
    z-index:20!important;
}
li.node-response div.body:after {
	content: '';
	position: absolute;
	top: -2px; 
    right: 0px;
	bottom: 0; 
    left: 0-22px;
    width: 40px!important;
    height: 40px!important;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    background-size: 100% 100%!important;
    z-index:20!important;
}
a.fleft:after {
	content: '';
    position: absolute;
	margin-left: -11.5%;
    width: 40px!important;
    height: 40px!important;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    background-size: 100% 100%!important;

}
div.photo-profile:after{
	content: '';
	position: absolute;
	top: -6px; 
    right: 0px;
	bottom: 0; 
    left: 10px;
	background-size: 100% 100%;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    z-index:20!important;
}
a.profile-avatar:after{
	content: '';
	position: absolute;
    top: 65px; 
    right: 0px;
	bottom: 0; 
    left: 14px;
    width: 70px;
    height: 70px;
	background: url(http://socialani.me/css/images/tape_admin.png) no-repeat;
    background-size: 100% 100%;
    z-index:20!important;
}

img.tape-admin{
display: none;
}
}
