css:
@-moz-document domain("soundcloud.com") {
/*Style by David Kuntz
Feel free to edit whatever you want but leave credit where credit is due*/

/*BG #000*/
.header,
.compactTrackListItem:hover,
.compactTrackList__moreLink.active,
.compactTrackList__moreLink:hover,
.compactTrackList__moreLink:focus,
.playControls__inner,
.playControls__icon,
.volume__sliderWrapper,
.sc-background-light,
.l-fixed-top-one-column > .l-top,
.trackItem.hover,
.trackItem.active,
.trackItem__actions .soundActions,
.playControls__bg,
button,
.sc-button,
.sc-button-download,
.sc-tag {
 background-color: #000 !important
}
.trackItem:not(.m-disabled).hover .trackItem__additional,
.trackItem:not(.m-disabled).active .trackItem__additional {
 background: linear-gradient(to right, #000, #000 17px) !important
}

/*BG #060606*/
#content,
.gritter-item,
.dialog,
.readMoreBadge__countWrapper,
.searchTitle,
.uploadMain__chooserContainer,
.compactTrackListItem.active,
.compactTrackListItem:visited,
.compactTrackListItem:focus,
.dropbar__content,
.chartTrack:hover,
.chartTrack.m-playing,
.searchTitle__text,
.l-footer,
.uploadMain__foot,
.tabs__tabs,
.g-modal-section,
.streamSidebar,
.modal__modal,
.userSidebar,
.audibleEditForm__form,
.editTrackItem__additional,
.g-upsell-container,
.sidebarInfoBox,
.l-sidebar-right,
.readMoreTile__countWrapper,
.listenLogin,
.featureChartPromo__planWrapper,
.conversation__actions,
.playlistConsumerSubUpsell__messageBox,
.ext-con-menu,
.commentFormDisabled,
.audibleEditForm__progress,
.gritter-item-wrapper,
.commentItem.m-creatorComment,
.profileUploadFooter,
.l-container.l-content,
.sc-background-white,
.statsOverview__separator,
.sc-aux-container,
.premiumProductInfo.m-highlighted,
.consumerSubContent__subDetailsSection,
.consumerSubContent__footnote {
 background: #060606 !important
}

/*BG #111*/
.commentForm__wrapper,
.linkMenu,
.streamHTUpsell,
.callout__bubble,
.callout__bubble::before,
.inboxItem:hover,
.inboxItem:focus,
.inboxItem.unread,
.inboxItem.active,
.webProfileControl__emptyIcon,
.recipientChooser .tokenInput__wrapper,
.tokenInput__token,
.queue,
.queue__itemWrapper,
.queue__itemsHeight {
 background: #111 !important
}

/*BOX SHDW #333*/
.gritter-item {
 box-shadow: 0px 0px 7px 1px #333 !important
}
.sc-button-dropdown:focus,
.sc-button-dropdown:focus:not(.sc-button-disabled):not(:disabled) {
 box-shadow: 0 0 0 4px #333
}

/*BRDR #333*/
.sc-border-light,
.commentForm__wrapper,
.uploadMain__foot,
.sc-tag,
.dialog,
.l-footer,
.sc-border-light-top,
.g-tabs,
.sc-border-light-bottom,
.collection.m-overview .collection__section:not(:last-child),
.searchPremiumContentFooter,
.searchTitle__text,
.chartsMain_listHeader,
.sc-border-light-right,
.dialog__arrow,
.l-listen-wrapper .l-about-rows,
.composeMessage__bottomWrapper,
.moreActions,
.g-modal-title-h1,
.sc-background-light,
.commentForm__input,
.sc-toggle,
.audibleBadgePlaceholder:before,
.sc-snippet-badge-grey,
.g-form-section-head,
select,
.sc-input,
.g-upsell-container,
.sc-border-dark,
.audibleTilePlaceholder:before,
.conversation__form,
.playlistConsumerSubUpsell__messageBox,
.l-signin .l-main,
.sc-button:hover,
.sc-button:focus,
.sc-button:disabled,
.sc-button:disabled:hover,
.sc-button-disabled,
.sc-button-disabled:hover,
.sc-button-disabled:focus,
.sc-button.sc-pending,
.sc-button.sc-pending:hover,
.sc-button.sc-pending:focus,
.moreActions__button:not(:last-child),
.moreActions__link,
.selectionModule,
.sc-button.sc-button-transparent,
.chartTracksEnd,
.ownActivity.large .ownActivity__user,
.premiumProductInfo,
.queue__panel,
.queueFallback__stationMode {
 border-color: #333 !important
}
.moreActions__group:not(:first-child) {
 border-color: #333 !important
}

/*#f50*/
a,
button,
.compactTrackListItem.active .compactTrackListItem__content .compactTrackListItem__trackTitle {
 color: #f50 !important
}
.header,
.playControls__inner {
 box-shadow: 0 0 7px 2px #f50
}
.volume__sliderWrapper {
 border: 1px solid #f50 !important
}

/*BODY*/

/*CREDITS TO JAN ERIK WAIDER FOR BACKGROUND IMAGE "DARK SEA". CHECK HIM OUT http://www.northlandscapes.com/en/, HE'S VERY TALENTED PHOTOGRAPHER*/
body {
 background-color: #000 !important;
 background-image: url('http://i.imgur.com/CikNd2M.jpg')!important;
 background-size: cover !important;
 background-repeat: no-repeat !important;
 background-attachment: fixed !important
}
.soundTitle__usernameText {
 color: #f90
}
p,
label,
h1,
h2,
div,
.compactTrackListItem__content,
.compactTrackListItem__number,
.compactTrackListItem__trackTitle,
input,
select,
.header__navMenu > li > a.selected,
.header > li > a:focus,
textarea {
 color: #888 !important
}
.compactTrackListItem.clickToPlay.active .compactTrackListItem__additional,
.compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional,
.compactTrackListItem.clickToPlay:focus .compactTrackListItem__additional,
.soundBadge__additional,
.truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after,
.truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after,
.createPlaylistSuggestion__addContainer {
 background: none
}
.streamSidebar,
.userSidebar,
.l-sidebar-right {
 box-shadow: 0 0 0 20px #060606
}
.playControls__icon,
.repeatControl,
.volume__button,
.listenEngagement {
 border: none
}
.volume__sliderWrapper::before,
.volume__sliderWrapper::after {
 border-color: transparent transparent #ff9900 #ff9900
}
.tagInput__wrapper,
input,
select,
textarea,
.queue__hide,
.queueItemView:hover,
.queueItemView.m-active {
 background-color: #292929 !important
}
.sc-tag:hover,
.sc-toggle {
 background-color: #666 !important
}
.modal__modal {
 visibility: visible !important
}
.modal,
.modal.modalWhiteout {
 background-color: rgba(0, 0, 0, 0.9) !important
}
.dialog__arrow,
.inbox__item:before {
 background-color: #333 !important
}
#content {
 padding-bottom: 50px !important
}
.l-container.l-content {
 box-shadow: 0 0 7px 3px #f50 !important;
}
body {
 padding-bottom: 0px
}
.headerSearch__submit {
 background-color: inherit
}
.listenEngagement {
 box-shadow: none
}
a,
span,
button,
h1,
h2,
h3,
h4,
h5,
h6,
p,
.g-nav-item.active > .g-nav-link,
.sc-type-medium {
 font-weight: 400 !important;
 text-shadow: 1px 1px 1px #000
}
.conversation__actions {
 box-shadow: 0 1px 0 #333
}
.playControls__control {
 width: 30px;
 height: 30px;
 margin: 9px 4px
}
.volume__button {
 width: 25px;
 height: 26px;
 margin-top: 10px
}
.volume__sliderWrapper {
 bottom: 30px;
 left: -3px
}
.repeatControl,
.shuffleControl,
.playControls__control {
 border-radius: 5px
}
.volume__button {
 border-radius: 30px
}
button.headerSearch__submit.submit.sc-ir,
.soundActions,
.sound__soundActions {
 background-color: transparent !important
}
.playControls__inner,
.playControls__bg {
 border-color: #000
}
button,
.sc-button,
.sc-button-download {
 border-color: #111
}
}

@-moz-document url-prefix("https://soundcloud.com/messages/") {
#content {
 min-height: 600px !important
}
}
