css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dreamwidth.org") {
  
  #canvas {
  }
  
  .comment-thread {
    margin-top: -6px;
  }
  
  .comment-depth-1 {
    margin-top: 35px !important;
  }
  
  .comment-depth-1 .header {
    border-top: 6px solid #c1272d !important;
  }
  
  
  hr {
    display: none;
  }
  
  .userpic img {
    width: 58px;
    height: 58px;
  }
  
  .comment-thread .header {
    border-right: none !important;
    border-bottom: none !important;
    border-top: 1px solid #c1272d;
    background: white !important;
  }
  
  .poster-anonymous .poster {
    float: left;
    padding-right: 10px;
    padding-bottom: 4px;
  }
  
  .no-userpic .comment-info {
    padding-top: 4px;
  }

  .comment-thread .contents {
    background: white;
    padding: 10px;
  }
  
  .comment-thread .footer {
    background: white;
    padding: 0px 10px 10px 10px;
    border-bottom: 1px solid #c1272d;
  }
  
  .partial {
    background: white;
    padding: 10px;
    border-bottom: 1px solid #c1272d;
    border-top: 1px solid #c1272d;
  }
}
