css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com") {
    .rc
    {
        position: relative;
        padding: 10px 10px;
    }
    .body
    {
      background: rgb(248, 248, 248) none repeat scroll 0% 0%;

    }
    .g 
    {
      background: #FFF;
      box-shadow: 0px 1px 2px 0px rgba(143, 143, 143, 0.8);
      transition: 0.3s all;
      -webkit-transition: 0.3s all;
      -moz-transition: 0.3s all;
      -o-transition: 0.3s all;
      -ms-transition: 0.3s all;
    }
    .g:hover
    {
      box-shadow: 0px 1px 4px 0px rgba(30, 30, 30, 0.8);
      transition: 0.3s all;
      -webkit-transition: 0.3s all;
      -moz-transition: 0.3s all;
      -o-transition: 0.3s all;
      -ms-transition: 0.3s all;
    }
    #rcnt
    {
      background: rgb(248, 248, 248) none repeat scroll 0% 0%;
    }
    div.sfbg, div.sfbgg {
        background-color: #006BD5;
    }
    #gb#gb a.gb_P, #gb#gb span.gb_P {
        color: #FFFFFF;
        font-weight: bold;
    }
    body.vasq .ab_tnav_wrp {
        height: 43px;
        background: #F8F8F8;
    }
    #cnt {
        background: #F8F8F8;
    }
    #appbar {
        background: #f8f8f8;
    }
    .srp #searchform {
        position: fixed;
    }
    #logo img {
        top: -4001px;
    }
    #logo {   
        background: url("http://i.imgur.com/wOV8jlE.png") !important;
        background-position: -105px -100px !important;
    }
    a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
       color: #006bd5;
       font-family: Tahoma, Geneva, sans-serif;
    }
    #res h3.r {
       text-decoration: none;
    }
    .a, cite, cite a:link, cite a:visited, .cite, .cite:link, #_bGc > i, .bc a:link {
    color: #5D85D7;
    }
}
