css:
@-moz-document domain("web.vma.vzw.com") {
html,
body .fullscreen,
.fullscreen #pageContents2,
.fullscreen #pageContents2 .buapContainer,
.fullscreen #pageContents2 .buapContainer #buapContents,
.fullscreen #pageContents2 .buapContainer #buapContents .messageContainer,
.fullscreen #pageContents2 .buapContainer #buapContents .messageContainer .messageForm,
.fullscreen #pageContents2 .buapContainer #buapContents .messageContainer .composeMessage {
    height: 100%;
    width: 100%;
    padding: 0px;
    margin: 0px;
    position: absolute;
    top: 0px;
    left: 0px;
    right: 0px;
    bottom: 0px;
    min-height: 0;
}
.fullscreen .settingsLinks,
.fullscreen .settingsBox,
.fullscreen .conversation-list,
.fullscreen .conversation-box {
    height: 100%;
}
.fullscreen .settingsBox,
.fullscreen .conversation-box {
    width: auto;
    overflow: hidden;
    min-width: inherit;
    float: none;
}
.fullscreen .conversation-list .convListWrapper,
.fullscreen .settingsLinks #tabLinks,
.fullscreen #vma-composerContent {
    position: absolute;
    top: 50px;
    bottom: 0px;
    height: auto;
    width: 100%;
}
.fullscreen #vma-composerContent {
    bottom: 50px;
    max-height: none;
}
.fullscreen #composer-buttons,
.fullscreen #vma-sendControls3 {
    position: absolute;
    bottom: 0px;
    width: 100%;
}
.fullscreen #sendMessageForm {
    position: absolute;
    bottom: 49px;
    width: 100%;
}
.fullscreen #vma-chatContentWrapper {
    position: absolute;
    bottom: 72px;
    top: 0px;
    width: 100%;
}
.fullscreen #vma-chatContent {
    height: 100%;
}
.fullscreen .new-message #vma-chatContentWrapper {
    top: 65px;
}
.fullscreen #pageContents2 .contentNav.clearfix {
    display: none;
}
.fullscreen #vma-composerTitle .conversation-header .arrow {
    margin: auto;
}
}
