css:
@-moz-document domain("theblaze.com") {
.main-carousel-container h2 {
    font-size: 50px;
    padding: 0 5px 5px 15px;
    text-transform: none;
    word-wrap: break-word;
    background: rgba(0, 0, 0, 0.56) !important;
}

* {
    background-color: transparent !important;
    border-color: #ff0000 !important;
    color: dimgray !important; 
    font-family: "Comic Sans MS", cursive, sans-serif !important;
    font-weight: bold !important;
}

a:hover, a:hover *:not(img) {
    color: #cc0000 !important;

}

html{background:url(https://image.tmdb.org/t/p/original/mlAFQfX0Pn6kyiuI5cZE7mwsS1D.jpg) no-repeat center fixed;background-size:cover;-o-background-size:cover;-webkit-background-size:cover;
    }

h2, h1 {
    font-size: 30px!important;
    color: #cc0000 !important;
}

article {
    margin-left: 10px!important ;
    background: rgba(0, 0, 0, 0.81) !important;
    padding: 8px 25px!important ;
    border-radius: 5px!important ;
    box-shadow: 0 0 25px 0px rgba(0, 0, 0, 0.3)!important ;
}

div.widget.widget-container.widget_top-headlines-widget, div.widget-closed.vertical-widget-faith.widget.widget-container.widget_vertical, div.widget-closed.vertical-widget-business.widget.widget-container.widget_vertical, div.widget-closed.vertical-widget-technology.widget.widget-container.widget_vertical, div.blog-widget.widget.widget-closed.widget-container, div.widget.wire-widget.widget-closed.widget-container, div.main-sub-header.clearfix, div.jspPane li, div.clearfix, footer_wrapper.clearfix, div.contribution-widget.widget-closed.widget.widget-container, div.widget.widget-container.widget_listenwidget {
    background: rgba(0, 0, 0, 0.81) !important;
    box-shadow: 0 0 25px 0px rgba(0, 0, 0, 0.3)!important ;
    }

div.menu-container.clearfix {
    background-color: #000 !Important;
}

.main-hottopics a {
    color: #c71f1f !Important;
}

p {
    color: #a9a9a9 !important;
}

.menu-secondary-container {
    background: rgba(0, 0, 0, 0.92) !important;
}

div.twelve-col-eight article {
    width: 636px;
}

a.title h5 {
    background: rgba(0, 0, 0, 0.34) !important;
    }

div.clearfix.read-more {
    background: rgba(0, 0, 0, 0.34) !important;
    }

div.clearfix.read-more a {
    color: #cc0000 !important;
    }

div.postContent p a {
    color: #1d1dce !important;
    }
}
