css:
@-moz-document domain("game.naruto-arena.com"), regexp("http://game\\\\.naruto-arena\\\\.com/index\\\\.php") {
body {
background: linear-gradient(silver,grey,orangered) fixed;
background-image: linear-gradient(silver,grey,orangered);
background-color: whitesmoke;
background-attachment: fixed;
background-repeat: repeat;
margin: auto !important;
padding: inherit;
max-width: none;
min-width: inherit;
width: inherit;
max-height: none;
min-height: inherit;
height: inherit;
position: fixed;
float: none;
text-align: center;
display: inherit;
}
div#flashcontent {
padding: inherit;
margin: auto !important;
max-width: none;
min-width: inherit;
width: inherit;
max-height: none;
min-height: inherit;
height: inherit;
position: fixed;
float: none;
text-align: center;
display: flex;
}
embed {
background-color: transparent !important;
background: none !important;
margin: auto !important;
max-width: none;
min-width: inherit;
width: 100%;
max-height: none;
min-height: inherit;
height: 100%;
z-index: 2;
position: fixed;
float: none;
text-align: center;
}
}
