css:
@-moz-document domain("trello.com") {
body .list-wrapper:nth-child(2) .list {
    background: #abe896;
}

body .list {
    padding-bottom: 12px;
}

body .list-wrapper:not(:nth-child(2)) .open-card-composer {
    display: none;
}

body .list-wrapper:nth-child(2) {
    width: 300px;
}

body .list-wrapper {
    width: 250px;
}

body .list-wrapper:nth-child(2) .list-header-num-cards, body .list-wrapper:nth-child(2) .open-card-composer {
    color: black;
}

body .list-wrapper:nth-child(2) .open-card-composer {
    margin: auto 6px;
}

body .list-wrapper:nth-child(2) .open-card-composer:hover {
    background-color: #5aac44;
}
}
