css:
@-moz-document domain(emonitoring.poczta-polska.pl) {

body {
    color: #d2d2d2;
    background-color: #1d1d1d;
}

a {
    color: #839dd2;
}

#page_content select, #page_content input[type="text"], #page_content input[type="email"], #page_content input[type="password"], #page_content textarea {
    background-color: #3e3e3e;
    color: #d4d2d2;
}

#tmenu > li.light > a, #tmenu > li > a {
    background: #65231c;
    color: #d4d2d2;
}

#tmenu > li.light > a:hover, #tmenu > li > a:hover {
    background: #7b4529;
    color: #d4d2d2;
}

#topb, #cookiesBar {
    background: #424242;
}

.btn-default, .form-control, .ginput_container input[type="text"], .ginput_container input[type="email"], .ginput_container select, .ginput_container textarea {
    color: #ccc;
    background-color: #3e3e3e
}

#top, #footer, #sidebar > div > div:nth-child(7) > div, #sidebar > div > div:nth-child(10) > div {
    color: #ccc;
    background-color: #7e7e7e
}

}
