css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pokemon-matome.net") {
  /* 変数定義 */
  :root {
    --height: 30px;
  }

  .commenttext {
    min-height: var(--height);
  }
  .commentright {
    height: var(--height);
  }
  /* cmnt1, cmnt2, ...   */
  [class^="cmnt"],
  [class*=" cmnt"] {
    background-size: contain;
    min-height: var(--height);
  }
}
