css:
@-moz-document url-prefix("https://www.okcoin.cn/") {
.mainArea .leftMenu li {
	padding-left: 0;
}

.accountinfo1 .nav2-sub1 li {
	padding: 0;
}

.mainArea .leftMenu li a, .accountinfo1 .nav2-sub1 li a {
	display: inline-block;
}

.mainArea .leftMenu li a {
	width: 132px;
	padding-left: 48px;
}

.accountinfo1 .nav2-sub1 li a span {
	padding: 0 20px;
}

.mainArea .leftMenu li a:hover, .accountinfo1 .nav2-sub1 li a:hover {
	background-color: #eee;
}
}
