css:
/* STYLE BY UNHOLYMASTER || TUMBLR & INSTAGRAM */

@-moz-document
domain("trees.ancestry.com"),
domain("trees.ancestry.com.au") {

#FooterRegion {
color: #a2a0a0;
background: #181717;
background: -webkit-linear-gradient(#181717, #000000); /* Safari 5.1 to 6.0 */
background: -o-linear-gradient(#181717, #000000); /* Opera 11.1 to 12.0 */
background: -moz-linear-gradient(#181717, #000000); /* Firefox 3.6 to 15 */
background: linear-gradient(#181717, #000000); /* Standard syntax */
}

#FormAddPerson .cardSubtitle, #hoverbox .hd h4, .calloutMenu .link, .calloutMenu a, .h1, .h2, .h3, .h4, .h5, a, h1, h2, h3, h4, h5, label.success::after, legend.success::after {
color: #9043cc;
}

#calloutContent, .treeViewerAwesome .hoverCard, .treeViewerAwesome .hoverCard .cardBody, .treeViewerAwesome .node-bdy, .treeViewerAwesome .nodeContent {
background: #181717;
}

.calloutMenu a:hover, a:hover {
color: #be7fef;
}

.iconFemale, .photo.iconFemale::before, .treeViewerAwesome .nodePhoto.iconFemale, .userCardImg.iconFemale, .userCardSize3 .userCardImg.iconFemale::before {
background: #e91e63;
color: #f79dbb;
}

.iconMale, .photo.iconMale::before, .treeViewerAwesome .nodePhoto.iconMale, .userCardImg.iconMale, .userCardSize3 .userCardImg.iconMale::before {
background: #1e7ee9;
color: #9dc7f7;
}

.treeViewerAwesome .line.marriage-female, .treeViewerAwesome .openedNav .female.thumbNode {
background: #e91e63;
}

.treeViewerAwesome .line.marriage-male, .treeViewerAwesome .openedNav .male.thumbNode {
background: #1e7ee9;
}

.pageHeader.bgColor1, .pageHeader.bgColor1~.pagePadded .treeViewerAwesome .breadCrumbs .breadCrumbList {
background: rgba(0, 0, 0, 0.8) !important;
}

.bgColor8, .body, .modalCustomPadding .modalHeader, .modalCustomPadding .modalSection, .page, .pageHeader.bgColor1~.pagePadded, .pageHeader.bgColor1~.pagePadded .treeViewerAwesome .breadCrumbs .breadCrumbList, .pageHeader.bgColor1~.pagePadded .treeViewerAwesome .openedNav .tNavWrap, body, page {
background: #000000;
}

.calloutMenu li, .userCardSize4 .userCardEvents, a.icon, h6.nodeTitle {
color: #a2a0a0;
}

.treeViewerAwesome .nodeContent .nodeInfo {
color: #585757;
}

.treeViewerAwesome .openedNav {
background: rgba(255,255,255,0.4);
}

#hoverbox, #hoverbox .bd, .addPersonModal .modalColumn.w70, .ancCol.modalColumn, .ancGridEqual.ancGridNoGutters, .ancGridEqual.ancGridPadded, .con:not(.bgDark), .conSecond:not(.bgDark), .modalCentered+#modalContents, div.ancCol w66, section {
background: #181717;
color: #a2a0a0;
}

span.yui-carousel-button {
background-image: url('http://i.imgur.com/kT4ReTE.png');
}

.form .radio:checked+label::before {
background: #9043cc;
box-shadow: inset 0 2px 1px #9043cc,inset 0 -2px 0 rgba(0,0,0,.2),inset 0 0 0 6px #9043cc,inset 0 0 0 10px #ffffff;
}

.ancBtn {
background: #9043cc;
background: -webkit-linear-gradient(#9043cc, #be7fef); /* Safari 5.1 to 6.0 */
background: -o-linear-gradient(#9043cc, #be7fef); /* Opera 11.1 to 12.0 */
background: -moz-linear-gradient(#9043cc, #be7fef); /* Firefox 3.6 to 15 */
background: linear-gradient(#9043cc, #be7fef); /* Standard syntax */
}

#save.ancBtn.lrg.full320, .ancBtn:hover {
background: #be7fef;
background: -webkit-linear-gradient(#be7fef, #9043cc); /* Safari 5.1 to 6.0 */
background: -o-linear-gradient(#be7fef, #9043cc); /* Opera 11.1 to 12.0 */
background: -moz-linear-gradient(#be7fef, #9043cc); /* Firefox 3.6 to 15 */
background: linear-gradient(#be7fef, #9043cc); /* Standard syntax */
}

}
